(178, 92, '_form', '<form action="">\n				<table class="contact-table">\n					<tr>\n						<th width="35%">貴社名</th>\n						<td width="65%">\n							[text text-company]\n						</td>\n					</tr>\n					<tr>\n						<th>部署名</th>\n						<td>\n							[text text-company]\n						</td>\n					</tr>\n					<tr>\n						<th>\n							<span>お名前</span><span class="badge">必須</span>\n						</th>\n						<td>\n							[text* text-name]\n						</td>\n					</tr>\n					<tr>\n						<th>\n							<span>ふりがな</span><span class="badge">必須</span>\n						</th>\n						<td>\n							[text* text-furigana]\n						</td>\n					</tr>\n					<tr>\n						<th>\n							<span>メールアドレス</span><span class="badge">必須</span>\n						</th>\n						<td>\n							[text* email-email]\n						</td>\n					</tr>\n					<tr>\n						<th>\n							<span>メールアドレス（確認用）</span><span class="badge">必須</span>\n						</th>\n						<td>\n							[text* email-email2]\n						</td>\n					</tr>\n					<tr>\n						<th>\n							<span>電話番号</span><span class="badge">必須</span>\n						</th>\n						<td>\n							[tel tel-phone]\n						</td>\n					</tr>\n					<tr>\n						<th>お問い合わせ種類</th>\n						<td>\n                                                    [radio type label_first default:1 "ご相談" "採用について" "その他"]\n                                                        <label class="radio-label">\n                                                               [radio radio-soudan default:1][radio radio-392 default:1]\n                                                               <div class="radio-widget"></div>\n                                                               <span>ご相談</span>\n                                                        </label>\n                                                        <label class="radio-label">\n                                                               [radio radio-saiyou default:1]\n                                                               <div class="radio-widget"></div>\n                                                               <span>採用について</span>\n                                                        </label>\n                                                        <label class="radio-label">\n                                                               [radio radio-sonota default:1]\n                                                               <div class="radio-widget"></div>\n                                                               <span>その他</span>\n                                                        </label>\n                                                </td>\n					</tr>\n					<tr>\n						<th>お問い合わせ内容</th>\n						<td>\n							[textarea textarea-message]\n						</td>\n					</tr>\n				</table>\n				\n				\n				<div class="btn-box">\n					[submit]\n				</div>\n\n			</form>'),
